import react, {useEffect} from 'react';
import axios from "axios";

const MODE_SAVE = 'SAVE';
const MODE_DEL = 'DELETE';
const MODE_UPDATE = 'UPDATE';
const MODE_SELECT = 'SELECT';

const initialState = [];

export const articleSave = (saveData) => (
	{
		type : MODE_SAVE,
		saveData : {
			"id": saveData.id,
			"count": saveData.count,
			"title": saveData.title,
			"content": saveData.content,
			"date": saveData.date
		}
	}
);

export const articleRemove = (id) => (
	{
		type : MODE_DEL,
	    "id": id,
	}
);

export const articleUpdate = (updateData) => (
	{
		type : MODE_UPDATE,
		updateData : {
			"id": saveData.id,
			"count": saveData.count,
			"title": saveData.title,
			"content": saveData.content,
			"date": saveData.date
		}
	}
);

export const selectRow = (id) => (
	{
		type : MODE_DEL,
	    "id": id,
	}
);


export default function ArticleReducer(state = initialState, action) {
	useEffect(() => {
		axios.get("/crud/list.json").then((res) => {
		  state = res.data.dataList;
		});
	  }, []);
	switch (action.type) {
		case 'ADD': {
			return [
				...state,
				{"id": action.id,
				"count": 0,
				"title": action.title,
				"content": action.content,
				"date": action.date}
			]
		}
		case 'UPDATE': {
            let state = state.map((item) => item.id === action.id ? {...item, ...action } : item);
			return [
			    state
			]
		}
		case 'DELETE': {
            let state = state.filter((item) => {return item.id !== action.id});
			return [
			    state
			]
		}
		case "READ" : {
			return [
			    state
			]
		}
		default:
			return state
	}
}
