import react, {useEffect} from 'react';
import axios from "axios";

const initialState = [];

export default function ArticleReducer(state = initialState, action) {
	useEffect(() => {
		axios.get("/crud/list.json").then((res) => {
		  state = res.data.dataList;
		});
	  }, []);
	  debugger;
	switch (action.type) {
		case 'ADD': {
			return [
				...state,
				{"id": action.id,
				"count": 0,
				"title": action.title,
				"content": action.content,
				"date": action.date}
			]
		}
		case 'UPDATE': {
            let state = state.map((item) => item.id === action.id ? {...item, ...action } : item);
			return [
			    state
			]
		}
		case 'DELETE': {
            let state = state.filter((item) => {return item.id !== action.id});
			return [
			    state
			]
		}
		case "READ" : {
			return [
			    state
			]
		}
		default:
			return state
	}
}
