import react, {useEffect} from 'react';
import axios from "axios";

const MODE_SAVE = 'SAVE';
const MODE_DEL = 'DELETE';
const MODE_UPDATE = 'UPDATE';
const MODE_SELECT = 'SELECT';

export const articleSave = (saveData) => (
	{
		type : MODE_SAVE,
		saveData : {
			"id": saveData.id,
			"count": saveData.count,
			"title": saveData.title,
			"content": saveData.content,
			"date": saveData.date
		}
	}
);

export const articleRemove = (id) => (
	{
		type : MODE_DEL,
	    "id": id,
	}
);

export const articleUpdate = (updateData) => (
	{
		type : MODE_UPDATE,
		updateData : {
			"id": updateData.id,
			"count": updateData.count,
			"title": updateData.title,
			"content": updateData.content,
			"date": updateData.date
		}
	}
);

export const selectRow = (id) => (
	{
		type : MODE_DEL,
	    "id": id,
	}
);

let initialState = {
	// let initData = {};
	// useEffect(() => {
	// 	axios.get("/crud/list.json").then((res) => {
	// 		initData.article = res.data.dataList;
	// 	});
	//   }, []);
	//   return initData;
	  article : [
		{
			id: "1",
			count : "0",
			title : "안녕하세요",
			content : "안녕 내용",
			"date" : "2022.02.01"
		}

	]
}


export default function ArticleReducer(state = initialState, action) {
	switch (action.type) {
		case MODE_SAVE: 
			return {
				...state,
				article : {"id": action.id,
				"count": 0,
				"title": action.title,
				"content": action.content,
				"date": action.date}
			}
		
		case MODE_UPDATE: 
			return {
				...state,
			    article : state.map((item) => item.id === action.id ? {...item, ...action } : item)
			}
		
		case MODE_DEL: 
			return {
				...state,
			    article : state.filter((item) => {return item.id !== action.id})
			}
		
		// case MODE_SELECT : 
		// 	return {
		// 	    ...state,
		// 		article : state.find(row => {row.id === action.id})
		// 	}
		default:
			return state
	}
}
