import react from 'react';
import axios from "axios";

const initialState = [];
useEffect(() => {
    axios.get("/crud/list.json").then((res) => {
	  initialState = res.data.dataList
    });
  }, []);

export default function articleReducer(state = initialState, action) {
	switch (action.type) {
		case 'ADD': {
			return [
				...state,
				{"id": action.id,
				"count": 0,
				"title": action.title,
				"content": action.content,
				"date": action.date}
			]
		}
		case 'UPDATE': {
            let state = state.map((item) => item.id === action.id ? {...item, ...action } : item);
			return [
			    state
			]
		}
		case 'DELETE': {
            let state = state.filter((item) => {return item.id !== action.id});
			return [
			    state
			]
		}
		case "READ" : {
			useEffect(() => {
				axios.get("/crud/list.json").then((res) => {
				  setDataList(res.data.dataList);
				});
			  }, []);
		}
		default:
			return state
	}
}
